import { tags } from "typia";

/**
 * Refresh token session information.
 *
 * Represents an active or revoked refresh token session. Used to display
 * session information to users for security monitoring. Excludes sensitive
 * fields like token_hash and user_id.
 *
 * Note: The revoked_at field is optional as it's null for active tokens.
 *
 * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
 */
export type ITodoListRefreshToken = {
  /**
   * Primary Key.
   *
   * Unique identifier for this refresh token record.
   */
  id: string & tags.Format<"uuid">;

  /**
   * Token expiration timestamp.
   *
   * Set to 30 days from issuance as per authentication requirements. After
   * this time, the token becomes invalid and user must log in again.
   */
  expires_at: string & tags.Format<"date-time">;

  /**
   * Timestamp when the refresh token was issued.
   *
   * Used for token age tracking and audit purposes.
   */
  created_at: string & tags.Format<"date-time">;

  /**
   * Timestamp when the token was revoked.
   *
   * Null for active tokens. Set when user logs out, changes password, or
   * explicitly revokes the token. Revoked tokens cannot be used even if not
   * yet expired.
   */
  revoked_at?: (string & tags.Format<"date-time">) | undefined;
};
