import { tags } from "typia";

/**
 * Session revocation result for logout from all devices.
 *
 * Returned after revoking all refresh tokens to show how many sessions were
 * terminated and confirm the operation succeeded.
 *
 * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
 */
export type ITodoListSessionRevocationResult = {
  /**
   * Number of sessions revoked.
   *
   * The count of refresh tokens that were invalidated by the logout from all
   * devices operation.
   */
  count: number & tags.Type<"int32"> & tags.Minimum<0>;

  /**
   * Revocation confirmation message.
   *
   * Confirms all sessions were successfully terminated and the user needs to
   * log in again on all devices.
   */
  message: string;
};
